import { SHOW_MODAL } from '../types'

const handlers = {
	[SHOW_MODAL]: (state, { payload }) => ({
		...state,
		isShow: true,
		imageInfo: payload
	}),
	DEFAULT: (state) => state
}

export const modalReducer = (state, action) => {
	const handle = handlers[action.type] || handlers.DEFAULT
	return handle(state, action)
}
